import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './style/css/style.css';

class App extends Component {
constructor() {
  super()
  this.state = { deck_id:'', image:'', value:'', result:'' }
}

componentDidMount() {     
  fetch('https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1')       
  .then(response => response.json())       
  .then(data => this.setState({ deck_id:data.deck_id }))
  .then(this.drawCard)
}

drawCard = () => { 
  fetch(`https://deckofcardsapi.com/api/deck/${this.state.deck_id}/draw/?count=1`)       
  .then(response => response.json())       
  .then(data => data.cards.map(card => {
    switch (card.value) {
    case '0': 
    card.value = '10';
    break;
    case 'ACE': 
    card.value = '11';
    break;
    case 'JACK': 
    card.value = '12';
    break;
    case 'QUEEN': 
    card.value = '13';
    break;
    case 'KING': 
    card.value = '14';
    break;
  } 
  this.setState({image:card.image, value:card.value})} ))
}

compareCards = (b) => {
  var previousCard = this.state.value;
  // this.drawCard()
  if(b === 'up') {
    if (previousCard < this.state.value) this.setState({result:'win'})
    else (this.setState({result:'lose'}))
  } else {
    if (previousCard > this.state.value) this.setState({result:'win'})
    else (this.setState({result:'lose'}))
  }
}

  render() {
    return (
      <div className="App" >
        <button onClick={this.compareCards('up')} className="button">Up</button>
        <button onClick={this.compareCards('down')} className="button">Down</button>
        <h1>{this.state.result}</h1> 
        <img className="" src={this.state.image} alt="" />
      </div>
    );
  }
}

export default App;
