}

compareCards = () => {
  var previousCard = this.state.value;
  switch (previousCard) {
    case '0': 
    previousCard = '10';
    break;
    case 'ACE': 
    previousCard = '11';
    break;
    case 'JACK': 
    previousCard = '12';
    break;
    case 'QUEEN': 
    previousCard = '13';
    break;
    case 'KING': 
    previousCard = '14';
    break;
  }
  document.getElementById('up').onClick = function() {
    this.drawCard;

  }
}

  render() {
    return (
      <div className="App" >
        <button onClick={this.drawCard} id="up" className="button">Up</button>
        <button onClick={this.drawCard} id="down" className="button">Down</button>
        <h1>{this.state.deck_id}</h1> 
        <img className="" src={this.state.image} alt="" />
      </div>
    );
  }
}

export default App;
