import React, { Component } from 'react';
import './style/css/style.css';
import Cards from './Cards';

class App extends Component {
constructor() {
  super()
  this.state = { cards:'' , deck_id:'', image:'', value:'' }
}

componentDidMount() {     
  fetch('https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1')       
  .then(response => response.json())       
  .then(data => this.setState({ deck_id:data.deck_id }));
}

drawCard = () => { 
  var image;
  var value;
  fetch(`https://deckofcardsapi.com/api/deck/${this.state.deck_id}/draw/?count=1`)       
  .then(response => response.json())       
  .then(data => data.cards.map(function(card) { image = card.image; value = card.value} ))
  .then(this.setState({image:image, value:value}))
  .then (console.log(this.state.image))
}

  render() {
    return (
      <div className="App">
        <Cards />
        <button onClick={this.drawCard} className="button">Up</button>
        <button className="button">Down</button>
        <h1>{this.state.deck_id}</h1> 
        <img className="" src={this.state.image} alt=""/>
      </div>
    );
  }
}

export default App;
