import React, { Component } from 'react';
import './App.css';

class App extends Component {
constructor() {
  super()
  this.state = { deck_id:'', image:'', value:'', result:'' }
}

componentDidMount() {     
  fetch('https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1')       
  .then(response => response.json())       
  .then(data => this.setState({ deck_id:data.deck_id }))
  .then(this.drawCard)
}


upButton = () => { 
  var prevCard = this.state.card.value
  fetch(`https://deckofcardsapi.com/api/deck/${this.state.deck_id}/draw/?count=1`)       
  .then(response => response.json())       
  .then(data => data.cards.map(card => {
  if (card.value === 0) { card.value = 10 }
          if (card.value === 'ACE') { card.value = 11 }
          if (card.value === 'JACK') { card.value = 12 }
          if (card.value === 'QUEEN') { card.value = 13 }
          if (card.value === 'KING') { card.value = 14 }
          if (prevCard > card.value) {
            console.log('win')
          } else (
            console.log('lose')
          )
          this.setState({ card: card })
          console.log(card.value)
        })
  )
}

