      <div className="App" >
        <button onClick={this.upButton} className="button">Up</button>
        <button  className="button">Down</button>
        <h1>{this.state.result}</h1> 
        <img className="" src={this.state.image} alt="" />
      </div>
    );
  }
}

export default App;
