import React, { Component } from 'react';
import './style/css/style.css';
import Button from './Button';
import Cards from './Cards';

class App extends Component {
constructor() {
  super()
  this.state = { operations: [] }
}

componentDidMount() {     
  fetch('https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1')       
  .then(response => response.json())       
  .then(data => this.setState({ deck_id:data.deck_id }));
}

drawCard() {   
  console.log('Hello world!')  
  fetch(`https://deckofcardsapi.com/api/deck/${this.state.deck_id}/draw/?count=1`)       
  .then(response => response.json())       
  .then(data => this.setState({ cards:data.cards,  image:data.cards.images.png, value:data.cards.value }));
}

  render() {
    return (
      <div className="App">
        <Cards />
        <button onClick={this.drawCard} className="button">Click</button>
        <h1>{this.state.deck_id}</h1> 
        <img src='https://deckofcardsapi.com/static/img/8D.png' />
      </div>
    );
  }
}

export default App;
