import React, { Component } from 'react';
import './style/css/style.css';
import Cards from './Cards';

class App extends Component {
constructor() {
  super()
  this.state = { cards:'' , deck_id:'', image:'', value:'' }
}

componentDidMount() {     
  fetch('https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1')       
  .then(response => response.json())       
  .then(data => this.setState({ deck_id:data.deck_id }))
  .then(this.drawCard)
}

drawCard = () => { 
  fetch(`https://deckofcardsapi.com/api/deck/${this.state.deck_id}/draw/?count=1`)       
  .then(response => response.json())       
  .then(data => data.cards.map(card => {this.setState({image:card.image, value:card.value})} ))
}

