  render() {
    return (
      <div className="App" >
        <Cards />
        <button onClick={this.drawCard} className="button">Up</button>
        <button onClick={this.drawCard} className="button">Down</button>
        <h1>{this.state.deck_id}</h1> 
        <img className="" src={this.state.image} alt=""/>
      </div>
    );
  }
}

export default App;
